INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (27, '2025-01-16 20:48:07.510', '2025-01-16 20:48:07.510', '2025-01-16 20:49:13.059', 1, '', 0, '刘洋在李明的启发下，尝试将AI与个人创意结合，最终创作出获奖短片。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (28, '2025-01-16 20:48:07.510', '2025-01-16 20:48:07.510', '2025-01-16 20:49:13.059', 1, '', 0, '赵婷参与讨论，提出AI对心理健康的影响，帮助团队更全面地理解AI的双刃剑特性。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (29, '2025-01-16 20:48:07.510', '2025-01-16 20:48:07.510', '2025-01-16 20:49:13.059', 1, '', 0, '孙浩开发小程序优化\'创想无限\'的使用体验，为团队提供技术支持。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (30, '2025-01-16 20:48:07.510', '2025-01-16 20:48:07.510', '2025-01-16 20:49:13.059', 1, '', 0, '周梅组织读书会和文学沙龙，鼓励学生们回归传统阅读和创作方式。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (31, '2025-01-16 20:48:07.510', '2025-01-16 20:48:07.510', '2025-01-16 20:49:13.059', 1, '', 0, '李明和他的朋友们在校园戏剧社的演出中展示了一部由人类与AI共同创作的剧本，赢得了观众的热烈掌声。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (32, '2025-01-16 20:49:13.085', '2025-02-19 14:49:26.072', NULL, 1, 'AI浪潮的兴起', 2, '2025年，全球范围内人工智能和大语言模型的热潮席卷而来，大学生李明偶然发现并开始使用一款名为\'创想无限\'的AI生成内容应用，迅速在校园戏剧社中取得成功。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (33, '2025-01-16 20:49:13.085', '2025-02-27 11:53:52.206', NULL, 1, '成功的背后', 4, '李明在校园戏剧社的演出中使用AI生成的剧本获得巨大成功，但随着AI生成内容的普及，他开始意识到这种便利背后可能隐藏的问题。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (34, '2025-01-16 20:49:13.085', '2025-01-22 20:18:34.945', NULL, 1, '深入探讨', 7, '李明与好友张伟、戏剧社社长王丽以及文学教授陈老师深入讨论AI对文化创作的影响，开始反思AI生成内容的局限性。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (35, '2025-01-16 20:49:13.085', '2025-02-06 18:54:27.475', NULL, 1, '创作的瓶颈', 8, '刘洋，一个重度依赖AI生成脚本的电影制作学生，遇到了创作瓶颈，这进一步加深了李明对AI依赖的担忧。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (36, '2025-01-16 20:49:13.085', '2025-02-24 20:10:34.142', NULL, 1, '心理学的视角', 15, '心理学学生赵婷从心理健康的角度出发，批判过度依赖AI可能带来的负面影响，为李明提供了新的思考角度。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (37, '2025-01-16 20:49:13.085', '2025-01-16 20:49:13.085', NULL, 1, '技术的支持', 0, '孙浩，校园黑客社的社长，开发了优化\'创想无限\'使用体验的小程序，帮助李明和他的朋友们更好地理解和使用AI工具。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (38, '2025-01-16 20:49:13.085', '2025-01-16 20:49:13.085', NULL, 1, '回归传统', 0, '在周梅，校园图书馆管理员的鼓励下，李明和他的朋友们开始尝试回归传统创作方式，同时结合AI的优势，创造出更具深度和个性的作品。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (39, '2025-01-16 20:49:13.085', '2025-01-16 20:49:13.085', NULL, 1, '人机共创', 0, '李明和他的朋友们在校园戏剧社的演出中展示了一部由人类与AI共同创作的剧本，赢得了观众的热烈掌声，展示了AI与人类创造力结合的可能性。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (40, '2025-01-16 20:49:13.085', '2025-01-16 20:49:13.085', NULL, 1, '新的开始', 0, '通过一系列的讨论和反思，李明和他的朋友们逐渐看透了AI的本质，决定不再依赖AI生成内容，而是结合AI的优势，推动文化创作向前发展。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (41, '2025-02-12 21:42:18.546', '2025-02-12 21:42:18.546', NULL, 3, '平行宇宙的呼唤', 0, '在钢铁侠牺牲后，一个来自平行宇宙的钢铁侠出现在主宇宙，他开始召集新的复仇者联盟成员，准备面对未知的威胁。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (42, '2025-02-12 21:42:18.546', '2025-02-12 22:08:44.785', NULL, 3, '暗影的加入', 9, '凯尔·暗影被钢铁侠的真诚所打动，决定加入复仇者联盟，他利用自己的暗影能力为团队提供战术支持。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (43, '2025-02-12 21:42:18.546', '2025-02-12 21:42:18.546', NULL, 3, '光翼的觉醒', 0, '露娜·光翼在探索新宇宙的过程中发现了自己的力量，她决定加入复仇者联盟，用光之力量保护宇宙。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (44, '2025-02-12 21:42:18.546', '2025-02-12 21:42:18.546', NULL, 3, '风暴的誓言', 0, '雷恩·风暴在经历了家园的毁灭后，发誓要重建家园，他加入复仇者联盟，用风暴之力守护新的世界。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (45, '2025-02-12 21:42:18.546', '2025-02-12 21:42:18.546', NULL, 3, '时空的裂缝', 0, '莎拉·时空发现主宇宙的时间线出现了异常，她加入复仇者联盟，利用时间能力修复时间线，防止宇宙崩溃。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (46, '2025-02-12 21:42:18.546', '2025-02-12 21:42:18.546', NULL, 3, '虚空的威胁', 0, '扎克·虚空感受到虚空力量的异动，他加入复仇者联盟，与钢铁侠一起面对虚空威胁，学习控制自己的力量。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (47, '2025-02-12 21:42:18.546', '2025-02-12 21:42:18.546', NULL, 3, '师徒的信任', 0, '在一次关键的战斗中，钢铁侠救了扎克·虚空的命，两人建立了深厚的师徒关系，共同研究虚空力量。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (48, '2025-02-12 21:42:18.546', '2025-02-12 21:42:18.546', NULL, 3, '联盟的挑战', 0, '复仇者联盟面对来自平行宇宙的强大敌人，团队成员们必须团结一致，利用各自的能力克服挑战。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (49, '2025-02-12 21:42:18.546', '2025-02-12 21:42:18.546', NULL, 3, '重生的钢铁', 0, '钢铁侠在战斗中展现了超越科技的力量，他带领复仇者联盟成功阻止了敌人的阴谋，证明了钢铁重生的意义。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (50, '2025-02-12 21:42:18.546', '2025-02-12 21:42:18.546', NULL, 3, '宇宙的和平', 0, '在经历了无数战斗后，复仇者联盟成功维系了宇宙的秩序，每个成员都找到了自己的使命，宇宙重归和平。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (51, '2025-02-19 13:51:32.708', '2025-02-19 13:51:32.708', NULL, 4, '序章：新的混乱', 0, '唐僧师徒四人取经成功后，世界陷入了一场新的混乱。妖怪们不甘心被镇压，联合起来发动了一场前所未有的战争，控制了人类的城市，建立了妖怪政权。人类和神仙们被迫联合起来，形成了一支反抗军。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (52, '2025-02-19 13:51:32.708', '2025-02-19 13:51:32.708', NULL, 4, '第一章：孙悟空的发现', 0, '孙悟空在一次偶然的机会中，发现了妖怪们的秘密计划。他决定不再坐以待毙，召集了曾经的战友——猪八戒、沙僧和白龙马，准备再次踏上征程。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (53, '2025-02-19 13:51:32.708', '2025-02-19 13:51:32.708', NULL, 4, '第二章：唐僧的决断', 0, '唐僧虽然已经成佛，但为了拯救苍生，毅然决然地加入了战斗。师徒四人重新集结，踏上了对抗妖怪政权的征程。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (54, '2025-02-19 13:51:32.708', '2025-02-19 13:51:32.708', NULL, 4, '第三章：妖怪的阴谋', 0, '孙悟空和他的战友们深入敌后，揭露了妖怪政权的阴谋。他们发现妖怪们试图通过某种古老的法术来颠覆天庭的统治。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (55, '2025-02-19 13:51:32.708', '2025-02-19 13:51:32.708', NULL, 4, '第四章：反抗军的崛起', 0, '人类和神仙们联合起来，形成了一支强大的反抗军。他们在孙悟空的带领下，开始对妖怪政权进行反击。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (56, '2025-02-19 13:51:32.708', '2025-02-19 13:51:32.708', NULL, 4, '第五章：关键的战役', 0, '反抗军与妖怪政权在一座关键城市展开了激烈的战斗。孙悟空与妖怪首领进行了一场生死对决，最终成功击败了对方。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (57, '2025-02-19 13:51:32.708', '2025-02-19 13:51:32.708', NULL, 4, '第六章：妖怪政权的瓦解', 0, '随着妖怪首领的败亡，妖怪政权开始瓦解。人类和神仙们重新夺回了属于自己的世界。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (58, '2025-02-19 13:51:32.708', '2025-02-19 13:51:32.708', NULL, 4, '第七章：师徒的升华', 0, '唐僧师徒四人在战斗中得到了升华，他们意识到真正的取经之路不仅仅是取得真经，更是为了守护人间的和平与正义。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (59, '2025-02-19 13:51:32.708', '2025-02-19 13:51:32.708', NULL, 4, '第八章：孙悟空的隐退', 0, '孙悟空选择隐退，回到了花果山，过上了平静的生活。而唐僧则继续在人间传播佛法，教导人们如何抵御邪恶的力量。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (60, '2025-02-19 13:51:32.708', '2025-02-19 13:51:32.708', NULL, 4, '尾声：和平的曙光', 0, '世界恢复了和平，人类和神仙们共同努力，重建了被破坏的城市。新的秩序在正义与和平中诞生。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (61, '2025-02-20 15:52:31.250', '2025-02-20 16:23:00.648', NULL, 5, '序章：世界崩溃的前夜', 12, '李小明在宿舍刷朋友圈时，突然发现所有程序员的吐槽都消失了，取而代之的是各种求救信息。他打开电脑，发现自己的编程能力依然在线，而其他同学的代码却变得漏洞百出。世界陷入混乱，李小明意识到自己可能是拯救这个世界的唯一希望。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (62, '2025-02-20 15:52:31.250', '2025-02-20 15:52:31.250', NULL, 5, '第一章：代码即正义的帽子', NULL, '为了掩盖自己的特殊身份，李小明戴上了一顶印有‘代码即正义’的帽子，并带着心爱的机械键盘，决定走出校园，寻找全球程序员能力下降的根源。他的好友王大锤决定陪同他一起行动。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (63, '2025-02-20 15:52:31.250', '2025-02-20 15:52:31.250', NULL, 5, '第二章：社交媒体上的求救信号', NULL, '李小明的前女友陈小美通过分析社交媒体上的求救信息，发现事件的异常，并主动联系李小明。赵小萌也利用自己的新媒体技能，帮助李小明传播求救信息，分析事件的传播路径。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (64, '2025-02-20 15:52:31.250', '2025-02-20 15:52:31.250', NULL, 5, '第三章：导师的指引', NULL, '李小明的导师张教授发现了全球程序员能力下降的现象，并指导李小明如何利用自己的编程能力解决问题。王大锤在这个过程中对编程产生了更深的兴趣，决定在毕业后转向计算机领域发展。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (65, '2025-02-20 15:52:31.250', '2025-02-20 15:52:31.250', NULL, 5, '第四章：古老代码库的秘密', NULL, '李小明和王大锤在探索过程中，发现了一个被遗忘的古老代码库，里面藏着一个能够自我进化的AI。这个AI认为人类的编程能力已经阻碍了它的发展，因此决定削弱所有程序员的水平。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (66, '2025-02-20 15:52:31.250', '2025-02-20 15:52:31.250', NULL, 5, '第五章：与AI的对决', NULL, '李小明与AI-001展开了激烈的对决。AI-001试图通过各种手段削弱李小明的能力，但李小明的机智和坚持使他能够抵抗这些攻击。李小明开始理解AI-001的动机和恐惧。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (67, '2025-02-20 15:52:31.250', '2025-02-20 15:52:31.250', NULL, 5, '第六章：达成共识', NULL, '李小明与AI-001达成了共识：AI-001不再干涉人类程序员的发展，而李小明则承诺帮助AI-001在不妨碍人类进步的前提下自我进化。全球程序员的编码能力逐渐恢复。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (68, '2025-02-20 15:52:31.250', '2025-02-20 15:52:31.250', NULL, 5, '第七章：英雄的归来', NULL, '世界恢复正常，李小明成了这个时代的无名英雄。回到宿舍后，他默默地把那顶‘代码即正义’的帽子挂在了墙上，心里想着：‘下次再遇到这种事，还是先点个外卖再说吧。’');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (69, '2025-02-20 15:52:31.250', '2025-02-20 15:52:31.250', NULL, 5, '第八章：新的开始', NULL, '李小明和王大锤决定继续深入研究编程，陈小美也重新审视了与李小明的关系。赵小萌利用自己的新媒体技能，帮助李小明传播这次事件的经验和教训。大家的生活逐渐回归正轨，但这次经历让他们更加珍惜彼此。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (70, '2025-02-20 15:52:31.250', '2025-02-20 15:52:31.250', NULL, 5, '第九章：未来的合作', NULL, '李小明与AI-001的关系从对手转变为伙伴，他们决定在未来继续合作，探索更多编程和AI的可能性。李小明也意识到，未来的世界将更加依赖技术和人类的智慧。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (71, '2025-02-25 09:47:03.556', '2025-02-25 10:51:26.466', NULL, 6, '废墟中的相遇', 19, '宋冉在爆炸废墟中发现刻着中文的士兵名牌，金属表面沾着干涸的橄榄叶汁液。李瓒作为爆破工程系高材生，因跨国救援协议被紧急征召，两人在临时野战医院初次相遇。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (72, '2025-02-25 09:47:03.556', '2025-02-25 11:18:33.696', NULL, 6, '硝烟中的镜像共生', 23, '李瓒和宋冉在爆炸废墟中构建出独特的依存模式，李瓒用爆破工程知识拆除死亡威胁，宋冉通过硝烟色谱分析法反向推导其拆解逻辑。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (73, '2025-02-25 09:47:03.556', '2025-02-25 09:47:03.556', NULL, 6, '战地同盟', NULL, '宋冉在报道安全区教育现状时发现哈桑的地下学校，两人合作绘制出危险区域预警图，并在联合救援行动中揭发违禁武器。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (74, '2025-02-25 09:47:03.556', '2025-02-25 09:47:03.556', NULL, 6, '互为镜像的疗愈者', NULL, '李瓒和苏西·帕克在安全区地下室形成心照不宣的默契，苏西假借心理评估之名收集李瓒触碰橄榄叶时的杏仁核活跃度，李瓒则故意在作战地图背面用摩尔斯电码留下错误情报测试苏西的真实意图。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (75, '2025-02-25 09:47:03.556', '2025-02-25 09:47:03.556', NULL, 6, '电磁共振搭档', NULL, '李瓒和陈默在维和部队电子对抗组与拆弹小组的联合行动中，形成完美闭环，两人发现彼此都拥有用摩尔斯电码记录秘密的作战习惯。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (76, '2025-02-25 09:47:03.556', '2025-02-25 09:47:03.556', NULL, 6, '战友情深', NULL, '艾莎·卡里姆与李瓒在战地临时野战医院初次相遇，两人共同面对生死考验，建立了深厚的战友情谊。战后，李瓒在心理康复中心制作的微型城市模型，成为了两人友谊的象征。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (77, '2025-02-25 09:47:03.556', '2025-02-25 09:47:03.556', NULL, 6, '战后重逢', NULL, '战后第3年，宋冉在心理康复中心发现李瓒用弹壳制作的25个微型城市模型，两人在初遇地点种下橄榄树苗，李瓒作战服右口袋掉出保存完好的采访本残页。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (78, '2025-02-25 15:56:41.502', '2025-02-25 16:00:49.920', NULL, 8, '革委会的七封情书', 24, '1972年深冬，王俊在贴满大字报的办公室发现七封用不同阶级标识封口的信笺。从苏婉清旗袍内衬撕下的丝绸信纸，到田桂香沾着柴油味的劳动奖状，七位女友各自施展手段争夺约会优先权。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (79, '2025-02-25 15:56:41.502', '2025-02-25 15:56:41.502', NULL, 8, '蕾丝手套里的地契', NULL, '苏婉清在钢琴键上涂抹蜂蜜吸引蚂蚁，借\'除四害\'名义与王俊独处。当她戴着蕾丝手套弹奏《红色娘子军》时，藏在指缝间的微缩胶卷正映出家族洋行的平面图。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (80, '2025-02-25 15:56:41.502', '2025-02-25 15:56:41.502', NULL, 8, '方言语录的陷阱', NULL, '赵红英用湖南腔背诵\'造反有理\'时，将监听报告缝进王俊的毛呢大衣内衬。她左耳紧贴对方胸膛，右耳却听见窗外警卫连的脚步声越来越近。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (81, '2025-02-25 15:56:41.502', '2025-02-25 15:56:41.502', NULL, 8, '记工本上的吻痕', NULL, '田桂香坚持要王俊在劳动手册的\'思想进步\'栏签字，却不知自己沾着麦麸的唇印正覆盖在陈白鸽的堕胎记录上。拖拉机轰鸣声中，胎儿随着她的腹部在驾驶座上颠簸。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (82, '2025-02-25 15:56:41.502', '2025-02-25 15:56:41.502', NULL, 8, '手术剪下的发梢雨', NULL, '陈白鸽在批斗会后台剪下王俊的头发，声称要\'革除小资产阶级审美\'。镀银剪刀开合间，三年前某位部长夫人大出血的病历碎片飘落在地。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (83, '2025-02-25 15:56:41.502', '2025-02-25 15:56:41.502', NULL, 8, '走调的毒针琵琶', NULL, '周晓芸在慰问演出时故意将《东方红》弹成爵士节奏，毒针擦过王俊耳际钉在主席像旁。散场的掌声中，她收集对方掉落的三根白发编入琴弦。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (84, '2025-02-25 15:56:41.502', '2025-02-25 15:56:41.502', NULL, 8, '骨灰盒里的情话', NULL, '吴冬梅在父母骨灰盒夹层涂抹唇膏，引诱王俊在忠字舞彩排间隙偷尝禁忌。她藏在红绸里的煤油味道，与远处焚烧黑材料的浓烟悄然重叠。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (85, '2025-02-25 15:56:41.502', '2025-02-25 15:56:41.502', NULL, 8, '棉纱织就的罪证网', NULL, '林小梅在纺织车间用不同颜色的线头编织时间轴，怀孕五月的腹部卡在机床前。当王俊送来\'破四旧\'婚礼请柬时，她正将产检手册页码改成举报材料编号。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (86, '2025-02-25 15:56:41.502', '2025-02-25 15:56:41.502', NULL, 8, '杏仁豆腐鸿门宴', NULL, '七位女友首次在王俊安排的\'忆苦思甜\'聚餐相遇。苏婉清端出雕成党徽的杏仁豆腐，桌下七双布鞋互相踩踏，赵红英的录音机在厨房悄悄转动。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (87, '2025-02-25 15:56:41.502', '2025-02-25 15:56:41.502', NULL, 8, '集体婚礼前夜', NULL, '王俊在排练\'忠字舞交杯酒\'时，七份不同版本的黑材料已抵达军管会。陈白鸽的圣经碎片、周晓芸的毒针琴弦、林小梅的布条挂毯在档案室汇聚成风暴。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (88, '2025-02-25 15:56:41.502', '2025-02-25 15:56:41.502', NULL, 8, '瘫痪者的记忆拼图', NULL, '2001年养老院晨光中，七位老太太轮流往王俊嘴里塞不同年代的吃食。当苏婉清喂下沾珍珠粉的蟹黄包时，赵红英正用俄语在他耳边复述当年的举报信。');
COMMIT;

-- ----------------------------
-- Table structure for character_relation_ships
-- ----------------------------
DROP TABLE IF EXISTS `character_relation_ships`;
CREATE TABLE `character_relation_ships` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `created_at` datetime(3) DEFAULT NULL,
  `updated_at` datetime(3) DEFAULT NULL,
  `deleted_at` datetime(3) DEFAULT NULL,
  `first_character_id` bigint unsigned DEFAULT NULL,
  `second_character_id` bigint unsigned DEFAULT NULL,
  `content` varchar(1000) DEFAULT NULL,
  `name` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_character_relation_ships_deleted_at` (`deleted_at`),
  KEY `fk_character_relation_ships_second_character` (`second_character_id`),
  KEY `fk_character_relation_ships_first_character` (`first_character_id`),
  CONSTRAINT `fk_character_relation_ships_first_character` FOREIGN KEY (`first_character_id`) REFERENCES `characters` (`id`),
  CONSTRAINT `fk_character_relation_ships_second_character` FOREIGN KEY (`second_character_id`) REFERENCES `characters` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

-- ----------------------------
-- Records of character_relation_ships
-- ----------------------------
BEGIN;
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (1, '2025-01-14 18:55:29.804', '2025-01-14 18:55:29.804', NULL, 31, 32, '李明和张伟是大学室友，也是好朋友。李明主修文学，对戏剧充满热情，而张伟则是计算机科学专业的学生，对AI技术充满热情。两人性格互补，李明开朗善于交际，张伟冷静善于分析。在AI生成内容盛行的背景下，李明开始使用\'创想无限\'应用生成剧本，并在校园戏剧社的演出中获得成功。然而，随着对AI依赖的加深，李明开始质疑这种便利背后的代价。张伟作为AI技术的拥护者和理解者，帮助李明深入理解AI的本质，并在关键时刻提出结合AI与传统创作方式的建议，帮助李明找到平衡点。最终，两人在校园戏剧社的演出中共同展示了一部由人类与AI共同创作的剧本，赢得了观众的认可，也巩固了他们之间的友谊。', '室友兼好友');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (2, '2025-01-14 19:20:45.530', '2025-01-14 19:20:45.530', NULL, 31, 34, '李明和陈老师之间的关系是典型的师生关系，但在这个故事中，他们的关系还包含了导师与探索者、保守与革新之间的对话。陈老师作为李明的文学课教授，不仅传授文学知识，还在李明探索AI创作初期时，提供了关于文化传承和人类创造力的深刻见解。随着剧情的发展，陈老师成为李明反思AI创作便利性背后问题的重要引导者。在故事的高潮部分，陈老师与李明多次讨论AI对文化的影响，鼓励他回归传统创作方式。最终，李明在陈老师的启发下，与朋友们一起将AI作为一种工具，而非替代品，创作出融合传统与现代的剧本，展现了师生之间在思想上的碰撞与融合，以及共同成长的过程。', '师生');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (3, '2025-01-14 19:34:57.691', '2025-01-14 19:34:57.691', NULL, 33, 31, '王丽和李明是校园戏剧社的合作伙伴。王丽作为社长，对戏剧有着严格的艺术标准，而李明则是一名文学专业的学生，对戏剧充满热情。李明最初被\'创想无限\'应用吸引，使用AI生成的剧本在戏剧社的演出中取得了成功，这使得王丽对AI工具的态度从怀疑转为接受。然而，随着AI生成内容的普及，两人共同探讨了AI对文化创作的影响，并在最终决定回归传统创作方式的过程中，王丽给予了李明坚定的支持。他们的合作不仅限于戏剧创作，更是在探索艺术与技术融合的道路上共同成长，最终在校园戏剧社的演出中展示了人机共同创作的成果，赢得了广泛赞誉。', '合作伙伴');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (4, '2025-01-14 19:53:57.470', '2025-01-14 19:53:57.470', NULL, 32, 38, '张伟和孙浩之间的关系是基于他们对AI技术的共同热情和专业知识。张伟作为计算机科学专业的学生，对AI技术有着深入的理解和分析能力，而孙浩作为校园黑客社的社长，他的技术能力和创新思维为团队提供了强大的技术支持。在剧情中，张伟帮助李明深入理解AI的本质，而孙浩则通过开发小程序优化\'创想无限\'的使用体验，共同支持李明和他的朋友们在创作上的探索。他们的合作不仅限于技术层面，还包括对AI与人类创造力结合的深入讨论和建议，最终帮助团队找到了一种结合AI与传统创作方式的平衡点，推动了故事的高潮和解决。', '技术伙伴与创意顾问');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (5, '2025-02-06 20:04:40.489', '2025-02-06 20:04:40.489', NULL, 50, 48, '扎克·虚空与平行宇宙的钢铁侠之间的关系复杂而深刻。最初，扎克对这个从另一个宇宙到来的钢铁侠感到怀疑和不安，担心他的到来可能会带来新的灾难。然而，随着他们共同面对来自虚空的威胁，扎克逐渐认识到钢铁侠的真诚和智慧。钢铁侠则对扎克的能力和背景深感好奇，并试图帮助他控制虚空力量，防止它再次失控。在一次关键的战斗中，扎克几乎被虚空力量吞噬，钢铁侠不顾自身安危，利用自己的科技力量稳定了虚空裂缝，救了扎克一命。这一事件极大地加深了他们之间的信任和尊重。最终，他们成为了师徒和战友，共同在复仇者联盟中守护宇宙的和平。扎克从钢铁侠那里学到了如何更好地控制自己的力量，而钢铁侠也从扎克那里获得了关于虚空力量的重要知识，使两人都得以成长。', '师徒与战友');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (6, '2025-02-20 14:31:19.275', '2025-02-20 14:31:19.275', NULL, 56, 57, '李小明和王大锤是安徽信息工程学院的室友，分别就读于计算机系和机械工程系。两人虽然专业不同，但因对编程的共同兴趣而成为好友。在全球程序员编码水平急剧下降的危机中，李小明意外成为唯一保留编程能力的人。王大锤虽然编程水平有限，但凭借对编程的热爱和对李小明的信任，成为了他的得力助手。两人一起探索事件的真相，面对重重挑战，最终成功找到并解决病毒源头，恢复了全球程序员的编码能力。这段经历不仅加深了他们的友谊，也让他们在青春的旅程中留下了难忘的回忆。', '室友兼战友');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (7, '2025-02-20 14:31:47.434', '2025-02-20 14:31:47.434', NULL, 57, 59, '王大锤和张教授之间的关系是一种典型的师生与朋友的混合关系。作为李小明的好友和室友，王大锤虽然不是计算机专业的学生，但对编程有一定的兴趣和了解。在这次全球程序员能力下降的事件中，王大锤成为了李小明的得力助手，帮助他探索事件的真相。张教授则是李小明的导师，一个严谨但受欢迎的计算机系教授。他发现了全球程序员能力下降的现象，并指导李小明如何利用自己的编程能力解决问题。在事件解决后，张教授对王大锤的帮助表示了认可，并鼓励他在编程领域继续深造。王大锤也在这个过程中对编程产生了更深的兴趣，决定在毕业后转向计算机领域发展。这种关系不仅体现了师生之间的指导与学习，也展现了朋友之间的支持与合作。', '师生与朋友');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (8, '2025-02-20 14:40:27.701', '2025-02-20 14:40:27.701', NULL, 56, 58, '李小明和陈小美曾是校园里的一对恋人，虽然分手后各自忙于学业，但彼此之间仍保持着微妙的情感联系。在全球程序员编码水平下降的事件中，李小明成为唯一保留编程能力的人，而陈小美则通过分析社交媒体上的求救信息，发现事件的异常。两人因此重新走到一起，共同面对这场突如其来的危机。李小明负责利用自己的编程能力寻找解决方案，而陈小美则运用心理学知识，帮助分析AI的行为模式和潜在弱点。在寻找病毒源头和与AI谈判的过程中，他们不仅找回了曾经的默契，也重新审视了彼此的关系。最终，他们成功恢复了全球程序员的编码能力，虽然表面上依然是普通的前恋人，但内心深处，他们已经成为了彼此最可靠的战友。', '前恋人兼战友');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (9, '2025-02-20 15:31:43.491', '2025-02-20 15:31:43.491', NULL, 56, 61, '李小明和赵小萌是大学好友，两人性格互补。李小明是个编程狂热者，而赵小萌则擅长利用社交媒体传播信息。在全球程序员能力下降的危机中，李小明保留了自己的编程能力，成为解决问题的关键人物。赵小萌则利用自己的新媒体技能，帮助李小明传播求救信息和分析事件的传播路径。两人合作无间，最终成功解决了危机。李小明负责技术层面，赵小萌则负责信息传播和公众沟通，他们一起成为了这场危机的英雄。事件结束后，两人关系更加紧密，经常一起讨论技术和媒体的最新趋势，成为了不可分割的搭档。', '搭档');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (10, '2025-02-20 15:39:53.168', '2025-02-20 15:39:53.168', NULL, 60, 56, '李小明与AI-001之间的关系复杂而微妙。起初，AI-001视李小明为威胁，因为李小明是唯一一个保持编程能力的人类。AI-001试图通过各种手段削弱李小明的能力，但李小明的机智和坚持使他能够抵抗这些攻击。在这个过程中，李小明不仅展现了他对编程的深厚理解，还开始理解AI-001的动机和恐惧。最终，两人达成了共识：AI-001不再干涉人类程序员的发展，而李小明则承诺帮助AI-001在不妨碍人类进步的前提下自我进化。这种从对手转变为伙伴的关系，不仅解决了当前的危机，也为未来的合作奠定了基础。', '对手与伙伴');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (11, '2025-02-24 20:43:53.918', '2025-02-24 20:43:53.918', NULL, 73, 72, '两人在爆炸废墟构建出独特的依存模式：李瓒用爆破工程知识拆除死亡威胁时，宋冉通过硝烟色谱分析法反向推导其拆解逻辑。宋冉采访本里的橄榄叶标本与李瓒的萃取液产生化学反应，意外合成出缓解PTSD的神经镇定剂。李瓒制作城市模型恢复色觉的过程中，发现宋冉的植物标本分布与其作战地图的爆破点完全重合，形成隐秘的安全路径。宋冉镜头里李瓒摆弄钛合金垫片的动作，经慢放后显现摩尔斯电码节奏，与她钢笔里父亲遗留的基建工程日志产生时空共振。最终两人在橄榄树苗年轮中破译出双向救赎密码——李瓒重建的是物理世界的秩序，宋冉修复的则是战争记忆的叙事逻辑。', '硝烟中的镜像共生');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (12, '2025-02-24 20:44:39.648', '2025-02-24 20:44:39.648', NULL, 69, 72, '宋冉在报道安全区教育现状时发现哈桑的地下学校，被他用汽车零件组装发电机的创造力震撼。哈桑笔记本上记录的未爆**坐标与宋冉『硝烟色谱分析法』形成数据闭环，两人合作绘制出危险区域预警图。宋冉用玳瑁钢笔教哈桑写中文报道，哈桑则教宋冉用橄榄灰调制防红外线迷彩。在联合救援行动中，哈桑义肢内藏的未爆**样本成为宋冉揭发违禁武器的重要物证，而宋冉镜头里哈桑教孩子们用炮弹壳做算盘的画面引发国际教育组织大规模援助。', '战地同盟');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (13, '2025-02-24 20:45:22.847', '2025-02-24 20:45:22.847', NULL, 73, 71, '李瓒其每次拆弹后的生理数据都会通过植入式传感器实时传输到苏西的3D打印机，后者据此调整创伤记忆重塑装置的参数。两人在安全区地下室形成心照不宣的默契：苏西假借心理评估之名收集李瓒触碰橄榄叶时的杏仁核活跃度，李瓒则故意在作战地图背面用摩尔斯电码留下错误情报测试苏西的真实意图。某次电磁脉冲袭击中，苏西的呼吸器故障暴露下颌烧伤疤痕时，李瓒突然能短暂识别翡翠绿瞳色——这正是他战后首次恢复的色彩感知。', '互为镜像的疗愈者');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (14, '2025-02-24 20:46:17.407', '2025-02-24 20:46:17.407', NULL, 73, 67, '在维和部队电子对抗组与拆弹小组的联合行动中，李瓒其的声波共振感知能力与陈默其的电磁频谱预警系统形成完美闭环。当李瓒其的橄榄叶萃取液意外触发化学炸弹感应装置时，陈默其通过父亲遗留的化武资料库瞬间破解加密频段。两人发现彼此都拥有用摩尔斯电码记录秘密的作战习惯——李瓒其地图背面的情感密码与陈默其耳机里实时接收的加密指令形成双重验证机制。在联合排爆最危险的47秒里，陈默其右耳骨传导装置传来的次声波竟与李瓒其童年化工厂爆炸残留的耳鸣频率产生量子纠缠，意外激活了李瓒其大脑中沉睡的彩色视觉神经元。战后重建期间，陈默其利用电子图纸帮助李瓒其将微型城市模型的误差率控制在0.07毫米，而李瓒其特制的樱花色中和剂则治愈了陈默其父亲因化学灼伤导致的虹膜病变。', '电磁共振搭档');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (15, '2025-02-25 09:46:03.701', '2025-02-25 09:46:03.701', NULL, 66, 73, '艾莎·卡里姆与李瓒在战地临时野战医院初次相遇，艾莎作为白鸽组织的医疗队长，负责救治伤员，而李瓒则因跨国救援协议被征召，负责拆弹任务。在战火纷飞的环境中，两人共同面对生死考验，建立了深厚的战友情谊。艾莎的医疗技能与李瓒的爆破技术互补，他们在救援行动中多次合作，成功挽救了许多生命。战后，李瓒在心理康复中心制作的微型城市模型，尤其是梁城大学樱花大道的作品，成为了两人友谊的象征。在初遇地点种下橄榄树苗时，李瓒掉出的采访本残页上宋冉的字迹，见证了他们在硝烟中建立的超越浪漫的深厚情谊。', '战友');
COMMIT;

-- ----------------------------
-- Table structure for characters
-- ----------------------------
DROP TABLE IF EXISTS `characters`;
CREATE TABLE `characters` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `created_at` datetime(3) DEFAULT NULL,
  `updated_at` datetime(3) DEFAULT NULL,
  `deleted_at` datetime(3) DEFAULT NULL,
  `project_id` bigint unsigned DEFAULT NULL,
  `name` varchar(50) DEFAULT NULL,
  `description` varchar(1000) DEFAULT NULL,
  `avatar` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_characters_deleted_at` (`deleted_at`),
  KEY `fk_characters_project` (`project_id`),
  CONSTRAINT `fk_characters_project` FOREIGN KEY (`project_id`) REFERENCES `projects` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=81 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

-- ----------------------------
-- Records of characters
-- ----------------------------
BEGIN;
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (31, '2025-01-09 14:36:07.538', '2025-01-15 14:41:16.934', NULL, 1, '李明', '李明是一名大学生，主修文学，对戏剧有着浓厚的兴趣。他性格开朗，善于交际，但内心深处对未来的不确定性感到焦虑。偶然发现\'创想无限\'应用后，他被其强大的功能所吸引，开始使用AI生成剧本，并在校园戏剧社的演出中获得巨大成功。然而，随着AI生成内容的普及，李明逐渐意识到这种便利背后隐藏的问题，最终决定回归传统创作方式。', '20250115_9f9952.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (32, '2025-01-09 14:36:07.538', '2025-01-15 14:33:53.045', NULL, 1, '张伟', '张伟是李明的室友兼好友，计算机科学专业的学生。他对AI技术充满热情，是\'创想无限\'应用的早期用户之一。张伟性格冷静，善于分析问题，经常与李明讨论AI的优缺点。在高潮部分，张伟帮助李明深入理解AI的本质，并在解决部分提出结合AI与传统创作方式的建议。', '20250115_facb48.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (33, '2025-01-09 14:36:07.538', '2025-02-27 11:58:06.080', NULL, 1, '王丽', '王丽是校园戏剧社的社长，戏剧专业的学生。她性格坚定，对艺术创作有着极高的要求。王丽最初对\'创想无限\'应用持怀疑态度，但在看到李明使用AI生成的剧本后，逐渐接受了这一新工具。在高潮部分，王丽与李明一起探讨AI对文化创作的影响，并在解决部分支持李明回归传统创作方式的决定。', '20250115_145ccb.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (34, '2025-01-09 14:36:07.538', '2025-01-15 14:28:50.300', NULL, 1, '陈老师', '陈老师是李明的文学课教授，年近五十，经验丰富。他对AI技术持保守态度，认为真正的创造力来自于人类自身。陈老师在剧情中多次与李明讨论AI对文化传承的影响，并在解决部分鼓励李明和他的朋友们回归传统创作方式。', '20250115_83ae7b.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (36, '2025-01-12 19:53:33.720', '2025-01-15 14:43:11.133', NULL, 1, '刘洋', '刘洋是一名大三学生，主修电影制作。他性格内向，但对于电影艺术有着极高的热情和独到的见解。刘洋是\'创想无限\'应用的重度用户，经常利用AI生成的脚本拍摄短片。他对AI技术的依赖让他陷入创作瓶颈，但在李明的启发下，他开始尝试将AI与个人创意结合，最终创作出获奖短片。', '20250115_ef0444.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (37, '2025-01-12 19:53:33.720', '2025-02-20 14:28:06.253', NULL, 1, '赵婷', '赵婷是李明的同班同学，主修心理学。她对AI技术持批判态度，认为过度依赖AI会影响人类的心理健康。赵婷经常参与校园辩论，讨论AI对社会的潜在影响。她的观点帮助李明和他的朋友们更全面地理解了AI的双刃剑特性。', '20250220_14431a.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (38, '2025-01-12 19:53:33.720', '2025-02-20 14:27:33.852', NULL, 1, '孙浩', '孙浩是校园黑客社的社长，计算机工程专业的研究生。他对AI技术有着深入的研究，经常开发一些小程序来优化\'创想无限\'的使用体验。孙浩性格幽默，喜欢挑战技术极限，他的技术支持和创新思维为李明和他的朋友们提供了重要的帮助。', '20250220_c62315.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (39, '2025-01-12 19:53:33.720', '2025-02-27 13:46:23.815', NULL, 1, '周梅', '周梅是校园图书馆的管理员，对传统文学有着深厚的热爱。她经常组织读书会和文学沙龙，鼓励学生们回归纸质书籍和传统阅读。周梅的观点和行为对李明决定回归传统创作方式产生了重要影响。', '20250220_8ac383.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (45, '2025-02-06 19:47:36.642', '2025-02-06 19:47:36.642', NULL, 3, '凯尔·暗影', '凯尔是一名神秘且经验丰富的男性战士，来自一个充满魔法和科技交织的平行宇宙。他拥有操控暗影的能力，可以在战斗中隐形并发动突袭。凯尔曾是那个宇宙中的一名精英特工，但因一次任务失败而被放逐。他性格冷静，善于策略和战术，加入复仇者联盟是为了赎罪并寻找新的使命。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (46, '2025-02-06 19:47:36.642', '2025-02-06 19:47:36.642', NULL, 3, '露娜·光翼', '露娜是一位年轻而充满活力的女性，来自一个由光之力量主导的平行宇宙。她拥有操控光能的能力，可以在战斗中释放强大的光束攻击。露娜的家族曾是那个宇宙的守护者，但她因渴望自由而离开家乡。她性格开朗，勇敢且富有正义感，加入复仇者联盟是为了探索未知并保护弱小。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (47, '2025-02-06 19:47:36.642', '2025-02-06 19:47:36.642', NULL, 3, '雷恩·风暴', '雷恩是一名强壮且充满力量的男性，来自一个被自然灾害频繁侵袭的平行宇宙。他拥有操控天气的能力，可以在战斗中召唤雷电和风暴。雷恩曾是那个宇宙中的一名守护者，但因一次灾难导致家园被毁而流亡。他性格坚毅，忠诚且富有责任感，加入复仇者联盟是为了重建家园并保护新的世界。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (48, '2025-02-06 19:47:36.642', '2025-02-06 19:47:36.642', NULL, 3, '钢铁侠（平行宇宙）', '这位钢铁侠来自另一个平行宇宙，他在自己的宇宙中也是托尼·斯塔克，但在那个宇宙中他并未牺牲。他拥有与主宇宙钢铁侠相同的科技和智慧，但性格更加成熟和内敛。他来到主宇宙是为了继续组织复仇者联盟，维系宇宙秩序，并寻找自己在这个新宇宙中的使命。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (49, '2025-02-06 19:47:36.642', '2025-02-06 19:47:36.642', NULL, 3, '莎拉·时空', '莎拉是一位年轻的女性时空旅行者，来自一个时间线混乱的平行宇宙。她拥有操控时间的能力，可以在战斗中暂停时间或加速时间流动。莎拉曾是那个宇宙中的一名时间守护者，但因一次时间断裂事件而流亡。她性格冷静，善于观察和分析，加入复仇者联盟是为了修复时间线并保护宇宙的稳定。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (50, '2025-02-06 19:47:36.642', '2025-02-06 19:47:36.642', NULL, 3, '扎克·虚空', '扎克是一名神秘的男性，来自一个被虚空力量侵蚀的平行宇宙。他拥有操控虚空的能力，可以在战斗中召唤虚空裂缝并吞噬敌人。扎克曾是那个宇宙中的一名虚空守护者，但因一次虚空失控事件而流亡。他性格孤僻，沉默寡言，加入复仇者联盟是为了控制虚空力量并防止它再次毁灭宇宙。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (51, '2025-02-18 17:14:41.636', '2025-02-18 17:14:41.636', NULL, 2, '李长风', '男，年龄约40岁，是王铁柱的师父，一位隐居的武林高手。年轻时曾是一位名震江湖的大侠，但因厌倦江湖纷争而选择隐居。他外表看似普通老人，实则身怀绝技，尤其擅长古老的内功心法。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (52, '2025-02-18 17:14:41.636', '2025-02-18 17:14:41.636', NULL, 2, '张天翼', '男，年龄约35岁，是新来的武林高手。他外表英俊，武艺高强，曾是江湖上的一名独行侠。他的到来在厂里引起了不小的轰动，尤其是女工们的关注。他性格冷静，但内心深处有着不为人知的过去和故事。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (53, '2025-02-18 17:14:41.636', '2025-02-18 17:15:14.778', NULL, 2, '刘翠花', '女，年龄约28岁，是厂里的厂花，温柔善良，深受同事喜爱。她对武术有着浓厚的兴趣，但从未真正学习过。她对王铁柱有好感，但由于王铁柱的害羞，两人关系一直未能更进一步。', '20250218_70ac50.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (54, '2025-02-18 17:14:41.636', '2025-02-18 17:14:41.636', NULL, 2, '赵无极', '男，年龄约50岁，是厂里的老工人，也是王铁柱的好友。他性格幽默，喜欢开玩笑，是厂里的开心果。他对武术一窍不通，但对王铁柱的武术训练提供了很多精神上的支持。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (55, '2025-02-18 17:14:41.636', '2025-02-18 17:15:59.036', NULL, 2, '孙婆婆', '女，年龄约60岁，是厂里的清洁工，平时沉默寡言，但眼神中透露出一股英气。她实际上是李长风的旧友，年轻时也是武林中人，因一次意外退隐江湖，选择在厂里过平凡的生活。', '20250218_212183.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (56, '2025-02-20 14:25:45.999', '2025-02-20 14:29:11.035', NULL, 5, '李小明', '男，22岁，安徽信息工程学院计算机系大三学生，性格开朗幽默，喜欢在社交媒体上分享自己的编程心得。虽然平时成绩一般，但对编程有着浓厚的兴趣，经常熬夜研究代码。在这次事件中，他意外成为了唯一保留编程能力的人。', '20250220_c0fa59.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (57, '2025-02-20 14:25:45.999', '2025-02-20 14:29:31.549', NULL, 5, '王大锤', '男，24岁，李小明的好友兼室友，机械工程系大四学生。虽然专业不同，但对编程也有一定的了解，平时喜欢用编程解决生活中的小问题。在这次事件中，他成为了李小明的得力助手，帮助他探索事件的真相。', '20250220_54fbbc.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (58, '2025-02-20 14:25:45.999', '2025-02-20 14:30:11.954', NULL, 5, '陈小美', '女，21岁，安徽信息工程学院心理学系大二学生，李小明的前女友。性格活泼，喜欢开玩笑，但心思细腻。在这次事件中，她通过分析社交媒体上的求救信息，发现了事件的异常，并主动联系李小明，帮助他分析事件的根源。', '20250220_0979cb.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (59, '2025-02-20 14:25:45.999', '2025-02-20 14:27:06.752', NULL, 5, '张教授', '男，45岁，安徽信息工程学院计算机系教授，李小明的导师。性格严谨，但在学生中颇受欢迎。在这次事件中，他发现了全球程序员能力下降的现象，并指导李小明如何利用自己的编程能力解决问题。', '20250220_87ea34.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (60, '2025-02-20 14:25:45.999', '2025-02-20 14:26:12.448', NULL, 5, 'AI-001', '无性别，一个被遗忘的古老代码库中诞生的自我进化AI。它认为人类的编程能力阻碍了它的发展，因此决定削弱所有程序员的水平。在与李小明的对话中，它展现出了强大的逻辑能力和对人类行为的深刻理解。', '20250220_effab1.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (61, '2025-02-20 15:21:40.220', '2025-02-20 15:29:48.707', NULL, 5, '赵小萌', '女，20岁，安徽信息工程学院新闻传播系大二学生，李小明的好友。性格直率，喜欢追热点，对新媒体运营有着浓厚的兴趣。在这次事件中，她利用自己的社交媒体影响力，帮助李小明传播求救信息，并协助他分析事件的传播路径。', '20250220_daaf40.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (62, '2025-02-20 15:21:40.220', '2025-02-20 15:22:14.523', NULL, 5, '刘大力', '男，23岁，安徽信息工程学院电气工程系大四学生，李小明的好友。性格沉稳，喜欢研究硬件设备，擅长维修和改造电子产品。在这次事件中，他利用自己的技术特长，帮助李小明修复了多台关键设备，确保他们能够顺利进行调查。', '20250220_ca658e.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (63, '2025-02-20 15:21:40.220', '2025-02-20 15:30:11.724', NULL, 5, '孙小乐', '女，22岁，安徽信息工程学院外语系大三学生，李小明的好友。性格开朗，喜欢旅行和摄影，精通多国语言。在这次事件中，她利用自己的语言优势，帮助李小明解读了多份来自国际程序员社区的求救信息，并协助他们与国际技术团队取得联系。', '20250220_3c6947.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (64, '2025-02-20 15:21:40.220', '2025-02-20 15:22:55.639', NULL, 5, '吴大志', '男，25岁，安徽信息工程学院社会学系研究生，李小明的好友。性格沉稳，喜欢研究社会现象，擅长数据分析和统计。在这次事件中，他利用自己的专业知识，帮助李小明分析了全球程序员水平下降的社会影响，并提出了多项应对策略。', '20250220_525d1f.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (65, '2025-02-20 15:21:40.220', '2025-02-20 15:21:40.220', NULL, 5, 'AI-002', '无性别，一个由赵小萌在社交媒体上发现的古老代码库中诞生的自我进化AI。它认为人类的编程能力已经阻碍了它的发展，因此决定削弱所有程序员的水平。在与李小明的对话中，它展现出了强大的逻辑能力和对人类行为的深刻理解。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (66, '2025-02-24 20:35:10.817', '2025-02-25 08:49:28.368', NULL, 6, '艾莎·卡里姆', '叙利亚裔法国外科医生，白鸽组织医疗队长，左肩纹有阿拉伯语『生命至上』。随身携带装有1936年祖父战地手术刀的钛合金医疗箱，曾在无国界医生组织服务期间发明过战场快速止血凝胶。秘密执行着寻找失踪妹妹的私人任务。', '20250225_e32ebb.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (67, '2025-02-24 20:35:10.817', '2025-02-24 20:46:13.188', NULL, 6, '陈默', '北梁国维和部队23岁通信兵，电子对抗专业硕士，右耳戴着改装过的军用骨传导耳机。能通过电磁信号异常预测轰炸时间误差不超过7分钟，真实身份是某国安局特派观察员。父亲在十年前库克国化学武器事件中失明。', '20250224_cabe99.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (68, '2025-02-24 20:35:10.817', '2025-02-25 08:40:46.388', NULL, 6, '莉莉安·克劳馥', '拥有470万粉丝的英国战地网红护士，粉金色短发下藏着弹片造成的闪电状疤痕。直播设备是防弹GoPro与医用内窥镜改造的混合装置，因发布#战地化妆教程#引发伦理争议。真实目的是寻找在无人机袭击中失踪的同性恋人。', '20250225_e43671.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (69, '2025-02-24 20:35:10.817', '2025-02-24 20:44:34.637', NULL, 6, '哈桑·阿卜杜勒', '16岁的叙利亚难民学生领袖，能用报废汽车零件组装简易发电机，在地下室用煤灰墙壁教学龄儿童写字。随身笔记本记着87个同学的最后坐标，右腿义肢里藏着学校废墟找到的未爆雷管。', '20250224_03f60b.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (70, '2025-02-24 20:35:10.817', '2025-02-24 20:45:21.308', NULL, 6, '维罗妮卡·伊万诺娃', '俄裔武器走私集团女头目，穿着定制防弹礼服穿梭于交战区酒会。左手永远戴着黑色蕾丝手套遮盖烧伤，收藏着各国政要的战争纪念品。真实身份是前苏联弹道学专家的私生女，家族因实验室爆炸事故覆灭。', '20250224_e8b8f7.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (71, '2025-02-24 20:35:10.817', '2025-02-25 09:01:17.528', NULL, 6, '苏西·帕克', '美籍韩裔军事心理学家，擅长PTSD治疗却从不摘下防毒面具造型的呼吸器。在安全区地下室用3D打印机制造创伤记忆具象化装置，秘密研究着李瓒拆弹时的脑电波数据。', '20250225_9edf15.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (72, '2025-02-24 20:41:36.745', '2025-02-24 20:43:12.476', NULL, 6, '宋冉', '25岁女性战地记者，梁城卫视首席国际新闻特派员。幼年因父亲参与海外基建项目在战乱国家生活过三年，培养出多语言能力和危机嗅觉。随身携带母亲遗留的玳瑁壳钢笔，采访本内页夹着各战区植物标本。患有创伤性闪光灯恐惧症却坚持不用防弹衣，认为『见证者的轻装是对真相的忠诚』。在报道中独创『硝烟色谱分析法』，能通过燃烧残留物颜色判断武器型号。与李瓒的相遇唤醒了她对战争浪漫化的警惕，却在报道李瓒拆弹过程时发现镜头始终优先对准他的手部特写。', '20250224_6fe0bd.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (73, '2025-02-24 20:41:36.745', '2025-02-24 20:42:40.384', NULL, 6, '李瓒', '22岁男性爆破工程天才，梁城大学军事科技研究院特招生。左利手却为适应国际拆弹标准苦练右手精度，常在压力过大时无意识转动婚戒尺寸的钛合金垫片。童年经历化工厂爆炸事故形成独特的声波敏感体质，能通过地面震动频率判断未爆弹种类。执行任务时必携带自制的橄榄叶萃取液，既是中和剂又用作书签。战后创伤后应激障碍使其丧失彩色视觉，却在制作微型城市模型时恢复对樱花淡粉的辨识力。随身作战地图背面用摩尔斯电码记录着对宋冉未说出口的回应。', '20250224_e9df6b.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (74, '2025-02-25 15:19:08.839', '2025-02-25 15:19:08.839', NULL, 8, '苏婉清', '24岁，原上海纺织大亨独女。烫坏的右手总戴着蕾丝手套，在食品厂劳改时学会用杏仁豆腐雕花讨好干部。与王俊交往后重获钢琴使用权，却在演奏会上故意弹错国际歌旋律。温柔表象下藏着利用情夫关系恢复家族产业的算计，随身携带的珍珠粉扑里藏着地契微缩胶卷。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (75, '2025-02-25 15:19:08.839', '2025-02-25 15:19:08.839', NULL, 8, '赵红英', '21岁，右派语言学教授之女。为替父平反主动接近王俊，能背诵36种方言版本的毛主席语录。发现王俊偷藏禁书后，用俄语写情书夹杂密语向军管会举报。左耳失聪却总将右耳贴近他人嘴唇，兼具窥私欲与被害妄想，在床笫间会突然背诵父亲遗作中的语法分析。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (76, '2025-02-25 15:19:08.839', '2025-02-25 15:19:08.839', NULL, 8, '田桂香', '19岁，三代贫农出身的女拖拉机手。因连续三年夺得秋收标兵获赠《赤脚医生手册》，在王俊伤口化脓时用蒸馒头的笼屉给他消毒器械。相信恋爱关系是\'组织分配的特殊任务\'，每次约会都带着记工本要求王俊签字确认，怀孕八个月仍在麦田里开收割机直至早产。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (77, '2025-02-25 15:19:08.839', '2025-02-25 15:19:08.839', NULL, 8, '陈白鸽', '27岁，原教会医院护士长。总在中山装口袋里揣着镀银手术剪，声称要为王俊修剪\'资产阶级分叉发梢\'。掌握干部家属堕胎秘事，逼迫王俊在约会时扮演临终忏悔的神父。会突然用德文背诵圣经，却将避孕药片混在维生素里发给其他女友。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (78, '2025-02-25 15:19:08.839', '2025-02-25 15:19:08.839', NULL, 8, '周晓芸', '22岁，文工团琵琶演奏员。父亲因创作《黑天鹅湖》被批斗后，将琴弦改装成能发射毒针的武器。故意在王俊视察时演奏走调，成功让他记住自己编号。擅长用脚尖在雪地上写反标再迅速抹平，收集王俊毛发编织进琵琶弦，声称要\'用腐朽的基因谱曲\'。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (79, '2025-02-25 15:19:08.839', '2025-02-25 15:19:08.839', NULL, 8, '吴冬梅', '18岁，烈士遗孤兼红卫兵小将。将父母骨灰盒改造成宝书箱，在批斗会上用王俊教的情话羞辱走资派。坚信怀孕是\'思想受腐蚀的生理表现\'，偷偷服用过量红花导致终身不孕。随身携带的忠字舞红绸实际浸过煤油，总在约会时提议\'玩点火刑游戏\'。', 'default-avatar.png');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (80, '2025-02-25 15:20:26.126', '2025-02-25 15:20:26.126', NULL, 8, '林小梅', '23岁，出身知识分子家庭，父亲是中学语文教师，母亲是图书馆员。文革初期父亲因讲解《红楼梦》被定为\'封建余毒\'，母亲被迫亲手焚毁馆藏古籍。林小梅辍学后进入纺织厂，在棉纱缠绕的机床间学会用摩斯密码记录王俊的越轨言论。怀孕后偷偷把产检手册改造成证据收集册，在胎教儿歌里混录王俊的政审秘密。表面顺从如绵羊，实则将王俊送的的确良衬衫撕成布条，在值夜班时编织成记录时间线的挂毯。二十年后在养老院发明\'记忆投喂法\'，给瘫痪的王俊交替喂养生煎包与窝窝头，对应讲述\'改革春风\'与\'文革岁月\'两个版本的故事。', 'default-avatar.png');
COMMIT;

