-- ----------------------------
-- Table structure for join_requests
-- ----------------------------
DROP TABLE IF EXISTS `join_requests`;
CREATE TABLE `join_requests` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `created_at` datetime(3) DEFAULT NULL,
  `updated_at` datetime(3) DEFAULT NULL,
  `deleted_at` datetime(3) DEFAULT NULL,
  `user_id` bigint unsigned NOT NULL,
  `team_id` bigint unsigned NOT NULL,
  `status` tinyint DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `idx_join_requests_deleted_at` (`deleted_at`),
  KEY `fk_join_requests_team` (`team_id`),
  KEY `fk_join_requests_user` (`user_id`),
  CONSTRAINT `fk_join_requests_team` FOREIGN KEY (`team_id`) REFERENCES `teams` (`id`),
  CONSTRAINT `fk_join_requests_user` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

-- ----------------------------
-- Records of join_requests
-- ----------------------------
BEGIN;
