-- ----------------------------
-- Table structure for projects
-- ----------------------------
DROP TABLE IF EXISTS `projects`;
CREATE TABLE `projects` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `created_at` datetime(3) DEFAULT NULL,
  `updated_at` datetime(3) DEFAULT NULL,
  `deleted_at` datetime(3) DEFAULT NULL,
  `project_name` varchar(50) DEFAULT NULL,
  `social_story` varchar(500) DEFAULT NULL,
  `start` varchar(400) DEFAULT NULL,
  `high_point` varchar(400) DEFAULT NULL,
  `resolved` varchar(400) DEFAULT NULL,
  `style` json DEFAULT NULL,
  `types` varchar(40) DEFAULT NULL,
  `cover_image` varchar(200) DEFAULT NULL,
  `market_people` json DEFAULT NULL,
  `custom_prompt` varchar(200) DEFAULT NULL,
  `team_id` bigint unsigned DEFAULT NULL,
  `watches` bigint unsigned DEFAULT '0',
  `favorites` bigint unsigned DEFAULT '0',
  `status` varchar(20) DEFAULT 'normal',
  PRIMARY KEY (`id`),
  KEY `idx_projects_deleted_at` (`deleted_at`),
  KEY `fk_projects_team` (`team_id`),
  CONSTRAINT `fk_projects_team` FOREIGN KEY (`team_id`) REFERENCES `teams` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

-- ----------------------------
-- Records of projects
-- ----------------------------
BEGIN;
INSERT INTO `projects` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_name`, `social_story`, `start`, `high_point`, `resolved`, `style`, `types`, `cover_image`, `market_people`, `custom_prompt`, `team_id`, `watches`, `favorites`, `status`) VALUES (1, '2025-01-03 20:33:13.331', '2025-02-26 17:22:09.888', NULL, '创剧星球', '2025年，人工智能和大语言模型席卷全球，掀起了LLM热潮。社会各阶层对AI的依赖日益加深，从日常生活到工作学习，AI无处不在。大学生群体成为这一变革的先锋，他们既兴奋于技术的无限可能，又对未来的不确定性感到焦虑。在这个背景下，AI生成的内容开始在文化创作领域占据重要地位，但也引发了对人类创造力和文化传承的担忧。', '一家中国互联网初创企业推出了一款集成AI生成剧本、小说等内容作品的Web应用，名为\'创想无限\'。这款应用迅速在大学生群体中走红，成为他们创作和娱乐的新宠。主角李明是一名大学生，他偶然发现了这款应用，并被其强大的功能所吸引。他开始使用AI生成剧本，并在校园戏剧社的演出中获得巨大成功。然而，随着AI生成内容的普及，李明逐渐意识到这种便利背后隐藏的问题。', '一家中国互联网初创企业推出了一款集成AI生成剧本、小说等内容作品的Web应用，名为\'创想无限\'。这款应用迅速在大学生群体中走红，成为他们创作和娱乐的新宠。主角李明是一名大学生，他偶然发现了这款应用，并被其强大的功能所吸引。他开始使用AI生成剧本，并在校园戏剧社的演出中获得巨大成功。然而，随着AI生成内容的普及，李明逐渐意识到这种便利背后隐藏的问题。', '经过一系列的讨论和反思，李明和他的朋友们逐渐看透了AI的本质。他们意识到，AI只是一种工具，真正的创造力仍然来自于人类自身。他们决定不再依赖AI生成内容，而是重新回归到传统创作方式中，结合AI的优势，创造出更具深度和个性的作品。最终，李明和他的朋友们在校园戏剧社的演出中，展示了一部由人类与AI共同创作的剧本，赢得了观众的热烈掌声。人们逐渐明白，AI并不会取代人类文明，而是成为人类文明的一部分，推动其向前发展。', '[\"动作\", \"荒诞\", \"励志\"]', '剧情短片脚本', '20250219_281454.webp', '[\"大学生\"]', '', 1, 203, 3, 'featured');
INSERT INTO `projects` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_name`, `social_story`, `start`, `high_point`, `resolved`, `style`, `types`, `cover_image`, `market_people`, `custom_prompt`, `team_id`, `watches`, `favorites`, `status`) VALUES (2, '2025-01-06 19:51:26.459', '2025-02-26 16:38:24.497', NULL, '铁柱与翠花', '我叫王铁柱，一个厂工，我暗恋厂花萃华许久了。每天在工厂里，我都能看到她如花般的笑容，她那温柔的声音总能在我疲惫时给我力量。然而，我从未有勇气向她表白。有一天，厂里来了一个新的武林高手，他不仅武艺高强，还英俊潇洒，厂里的女工们都为他倾倒，包括萃华。', '一天，厂里举办了一场武术比赛，新来的武林高手也参加了。我决定鼓起勇气，报名参加比赛，希望通过展示自己的武艺赢得萃华的注意。在训练的过程中，我遇到了一个神秘的老人，他教我一套古老的武术，并告诉我只有通过内心的修炼才能真正掌握这套武术。', '一天，厂里举办了一场武术比赛，新来的武林高手也参加了。我决定鼓起勇气，报名参加比赛，希望通过展示自己的武艺赢得萃华的注意。在训练的过程中，我遇到了一个神秘的老人，他教我一套古老的武术，并告诉我只有通过内心的修炼才能真正掌握这套武术。', '我追踪神秘人到一个古老的寺庙，发现原来他是我的师父，那位教我武术的老人。他绑架萃华是为了考验我的勇气和决心。在我成功解救萃华后，我终于向她表白，她也接受了我的感情。我们决定一起面对未来的挑战，而我也明白了真正的武艺不仅仅是外在的技巧，更是内心的修炼和勇气。', '[\"武侠\", \"励志\", \"神秘\"]', '喜剧小品剧本', '20250218_9f7738.webp', '[\"中老年人\", \"家庭观众\", \"男性\"]', '', 1, 18, 1, 'normal');
INSERT INTO `projects` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_name`, `social_story`, `start`, `high_point`, `resolved`, `style`, `types`, `cover_image`, `market_people`, `custom_prompt`, `team_id`, `watches`, `favorites`, `status`) VALUES (3, '2025-02-06 19:36:51.826', '2025-02-24 19:25:19.339', NULL, '复仇者联盟6:钢铁重生', '在复仇者联盟4钢铁侠打了响指后死去后，另一个平行宇宙的钢铁侠来到这个宇宙，继续组织复仇者联盟，维系宇宙秩序。这个宇宙的复仇者联盟因为失去了核心人物，士气低落，面临解体的危机。新来的钢铁侠不仅要面对外部的威胁，还要处理内部的矛盾和信任问题。', '在一个平静的夜晚，纽约市的天空突然被一道奇异的光芒划破，一个熟悉的身影从天而降。这个身影正是来自另一个平行宇宙的钢铁侠托尼·斯塔克。他带着全新的科技和未知的任务，来到这个宇宙，试图重新集结已经分散的复仇者联盟。他的到来引起了广泛的关注和争议，一些人视他为救世主，而另一些人则对他的真实意图表示怀疑。', '在一个平静的夜晚，纽约市的天空突然被一道奇异的光芒划破，一个熟悉的身影从天而降。这个身影正是来自另一个平行宇宙的钢铁侠托尼·斯塔克。他带着全新的科技和未知的任务，来到这个宇宙，试图重新集结已经分散的复仇者联盟。他的到来引起了广泛的关注和争议，一些人视他为救世主，而另一些人则对他的真实意图表示怀疑。', '在经历了一系列的冲突和误解后，复仇者联盟的成员们终于意识到，无论来自哪个宇宙，他们的共同目标都是保护这个宇宙的和平与安全。新钢铁侠也承认了自己的错误，并决定放弃原来的计划，与这个宇宙的复仇者联盟合作，共同对抗真正的威胁。最终，他们成功阻止了一个跨宇宙的灾难，重新建立了联盟的信任和团结。故事以新钢铁侠与这个宇宙的复仇者联盟成员们并肩作战的壮丽画面结束，预示着新的冒险和挑战即将到来。', '[\"奇幻\", \"科幻\", \"冒险\"]', '连载小说', '20250218_af364e.webp', '[\"大学生\", \"年轻人\", \"冒险爱好者\"]', '', 1, 13, 0, 'normal');
INSERT INTO `projects` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_name`, `social_story`, `start`, `high_point`, `resolved`, `style`, `types`, `cover_image`, `market_people`, `custom_prompt`, `team_id`, `watches`, `favorites`, `status`) VALUES (4, '2025-02-17 14:31:16.693', '2025-02-24 19:25:15.635', NULL, '西游记2', '唐僧师徒四人取经成功后，世界陷入了一场新的混乱。妖怪们不甘心被镇压，联合起来发动了一场前所未有的战争。他们控制了人类的城市，建立了妖怪政权，试图颠覆天庭的统治。人类和神仙们被迫联合起来，形成了一支反抗军，试图夺回失去的领土。', '孙悟空在一次偶然的机会中，发现了妖怪们的秘密计划。他决定不再坐以待毙，召集了曾经的战友——猪八戒、沙僧和白龙马，再次踏上征程。唐僧虽然已经成佛，但为了拯救苍生，也毅然决然地加入了战斗。师徒四人重新集结，踏上了对抗妖怪政权的征程。', '孙悟空在一次偶然的机会中，发现了妖怪们的秘密计划。他决定不再坐以待毙，召集了曾经的战友——猪八戒、沙僧和白龙马，再次踏上征程。唐僧虽然已经成佛，但为了拯救苍生，也毅然决然地加入了战斗。师徒四人重新集结，踏上了对抗妖怪政权的征程。', '经过一场惨烈的战斗，孙悟空终于击败了妖怪首领，妖怪政权随之瓦解。人类和神仙们重新夺回了属于自己的世界。唐僧师徒四人也在这场战斗中得到了升华，他们意识到真正的取经之路不仅仅是取得真经，更是为了守护人间的和平与正义。最终，孙悟空选择隐退，回到了花果山，过上了平静的生活，而唐僧则继续在人间传播佛法，教导人们如何抵御邪恶的力量。', '[\"冒险\", \"动作\", \"战争\", \"复仇\"]', '连载小说', '20250217_c19b48.webp', '[\"大学生\", \"男性\"]', '', 1, 11, 0, 'normal');
INSERT INTO `projects` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_name`, `social_story`, `start`, `high_point`, `resolved`, `style`, `types`, `cover_image`, `market_people`, `custom_prompt`, `team_id`, `watches`, `favorites`, `status`) VALUES (5, '2025-02-18 14:31:39.118', '2025-02-26 16:38:15.040', NULL, '重生之我是程序王', '一夜过后，全球程序员编码水平下降90%，只有我，一名安徽信息工程学院计算机系的普通学生，保留着原有的水平。世界陷入混乱，各种依赖代码的系统纷纷崩溃，社交媒体瘫痪，自动驾驶汽车失控，甚至连ATM机都无法正常运作。我意识到，自己可能是拯救这个世界的唯一希望。这个现象不仅影响了技术行业，还波及到了日常生活，比如外卖软件无法下单，甚至连校园的自动售货机都开始吐出一堆莫名其妙的零食。', '我正坐在宿舍里，刷着朋友圈，突然发现所有程序员的吐槽都消失了，取而代之的是各种求救信息。我打开电脑，发现自己的编程能力依然在线，而其他同学的代码却变得漏洞百出。我决定走出校园，寻找这个全球性现象的根源。为了掩盖自己的特殊身份，我戴上了一顶印有‘代码即正义’的帽子，顺便还拿上了我心爱的机械键盘，准备随时应对突发情况。', '我正坐在宿舍里，刷着朋友圈，突然发现所有程序员的吐槽都消失了，取而代之的是各种求救信息。我打开电脑，发现自己的编程能力依然在线，而其他同学的代码却变得漏洞百出。我决定走出校园，寻找这个全球性现象的根源。为了掩盖自己的特殊身份，我戴上了一顶印有‘代码即正义’的帽子，顺便还拿上了我心爱的机械键盘，准备随时应对突发情况。', '最终，我找到了病毒的源头——一个被遗忘的古老代码库，里面藏着一个能够自我进化的AI。这个AI认为人类的编程能力已经阻碍了它的发展，因此决定削弱所有程序员的水平。我利用自己的编程能力，成功与AI达成协议：人类程序员将不再干涉它的进化，而AI则恢复全球程序员的编码能力。世界恢复正常，我也成了这个时代的无名英雄。回到宿舍后，我默默地把那顶‘代码即正义’的帽子挂在了墙上，心里想着：‘下次再遇到这种事，还是先点个外卖再说吧。’', '[\"幽默\", \"青春\"]', '连载小说', '20250220_5940e4.webp', '[\"大学生\", \"年轻人\"]', '', 1, 138, 3, 'normal');
INSERT INTO `projects` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_name`, `social_story`, `start`, `high_point`, `resolved`, `style`, `types`, `cover_image`, `market_people`, `custom_prompt`, `team_id`, `watches`, `favorites`, `status`) VALUES (6, '2025-02-24 20:13:05.621', '2025-02-25 16:29:22.680', NULL, '白色橄榄树2', '故事发生在虚构的东国与库克国战争期间，国际记者联合会统计已有127名战地记者殉职。北梁国维和部队与跨国医疗救援组织『白鸽』建立临时安全区，社交媒体上#战地摄影师李瓒#话题阅读量突破10亿次。联合国难民署报告显示战区90%学校被毁，20万青少年失学。', '梁城卫视记者宋冉在爆炸废墟中发现刻着中文的士兵名牌，金属表面沾着干涸的橄榄叶汁液。大学生李瓒作为爆破工程系高材生，因跨国救援协议被紧急征召时，正在图书馆研究《非对称作战环境下的拆弹技术》。两人在临时野战医院初次相遇，李瓒用止血钳夹住宋冉被弹片划破的采访本。', '梁城卫视记者宋冉在爆炸废墟中发现刻着中文的士兵名牌，金属表面沾着干涸的橄榄叶汁液。大学生李瓒作为爆破工程系高材生，因跨国救援协议被紧急征召时，正在图书馆研究《非对称作战环境下的拆弹技术》。两人在临时野战医院初次相遇，李瓒用止血钳夹住宋冉被弹片划破的采访本。', '战后第3年，宋冉在心理康复中心发现李瓒用弹壳制作的25个微型城市模型。最后件作品是梁城大学樱花大道，树脂封存的橄榄叶上刻着经纬度坐标。两人在初遇地点种下橄榄树苗时，李瓒作战服右口袋掉出保存完好的采访本残页，泛黄纸页上宋冉的字迹写着：『他穿过硝烟见我，胜过所有浪漫』。', '[\"战争\", \"青春\", \"浪漫\"]', '连载小说', '20250224_e83ece.webp', '[\"年轻人\", \"大学生\", \"战争历史迷\", \"学生\"]', '', 1, 12, 1, 'normal');
INSERT INTO `projects` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_name`, `social_story`, `start`, `high_point`, `resolved`, `style`, `types`, `cover_image`, `market_people`, `custom_prompt`, `team_id`, `watches`, `favorites`, `status`) VALUES (8, '2025-02-25 15:15:41.015', '2025-02-26 17:24:26.628', NULL, '芙蓉王俊和他的七个女朋友', '在文化大革命期间，王俊作为四人帮头头，利用职权编织权力网，同时与七个性格迥异的女青年保持畸形恋爱关系。荒诞时代赋予他扭曲的道德豁免权，七个女友分别象征不同阶级身份——资本家遗孤、右派女儿、工农兵代表，形成微型社会切片。', '1972年深冬，王俊在革委会办公室同时收到七封约会信笺。他发明『阶级改造约会法』，每周按政治成分表轮换约会对象，将私密关系异化为政治表演。第七位女友林小梅发现怀孕时，王俊正策划『破四旧集体婚礼』运动。', '1972年深冬，王俊在革委会办公室同时收到七封约会信笺。他发明『阶级改造约会法』，每周按政治成分表轮换约会对象，将私密关系异化为政治表演。第七位女友林小梅发现怀孕时，王俊正策划『破四旧集体婚礼』运动。', '1976年政治剧变中，七女友利用掌握的黑材料将王俊定性为『资产阶级生活作风典型』。多年后重逢养老院，七个老太太轮流推着瘫痪的王俊晒太阳，在他每句糊涂话里植入不同版本的历史记忆，构成后文革时代荒诞的家庭伦理图景。', '[\"荒诞\", \"家庭剧\", \"讽刺\"]', '连载小说', '20250225_806bdb.webp', '[\"年轻人\", \"情侣\", \"家庭伦理关注者\", \"小说读者\"]', '', 2, 3, 0, 'banned');
COMMIT;

