-- ----------------------------
-- Table structure for chapter_versions
-- ----------------------------
DROP TABLE IF EXISTS `chapter_versions`;
CREATE TABLE `chapter_versions` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `created_at` datetime(3) DEFAULT NULL,
  `updated_at` datetime(3) DEFAULT NULL,
  `deleted_at` datetime(3) DEFAULT NULL,
  `user_id` bigint unsigned DEFAULT NULL,
  `chapter_id` bigint unsigned DEFAULT NULL,
  `content` text,
  `optimized_content` varchar(1000) DEFAULT NULL,
  `audio_path` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_chapter_versions_deleted_at` (`deleted_at`),
  KEY `fk_chapter_versions_chapter` (`chapter_id`),
  KEY `fk_chapter_versions_user` (`user_id`),
  CONSTRAINT `fk_chapter_versions_chapter` FOREIGN KEY (`chapter_id`) REFERENCES `chapters` (`id`),
  CONSTRAINT `fk_chapter_versions_user` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

