-- ----------------------------
-- Table structure for chapters
-- ----------------------------
DROP TABLE IF EXISTS `chapters`;
CREATE TABLE `chapters` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `created_at` datetime(3) DEFAULT NULL,
  `updated_at` datetime(3) DEFAULT NULL,
  `deleted_at` datetime(3) DEFAULT NULL,
  `project_id` bigint unsigned DEFAULT NULL,
  `tittle` varchar(50) DEFAULT NULL,
  `version_id` bigint unsigned DEFAULT NULL,
  `description` varchar(200) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_chapters_deleted_at` (`deleted_at`)
) ENGINE=InnoDB AUTO_INCREMENT=41 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- ----------------------------
-- Records of chapters
-- ----------------------------
BEGIN;
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (1, '2025-01-16 20:45:58.680', '2025-01-16 20:45:58.680', '2025-01-16 20:47:24.718', 1, '', 0, '2025年，AI技术全面渗透大学生活。李明偶然发现\'创想无限\'应用，被其强大的剧本生成功能吸引，开始在校园戏剧社使用AI生成剧本，迅速获得成功。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (2, '2025-01-16 20:45:58.680', '2025-01-16 20:45:58.680', '2025-01-16 20:47:24.718', 1, '', 0, '李明的AI生成剧本在校园戏剧社大受欢迎，但他逐渐意识到AI生成内容的同质化问题，开始对AI的创造力产生怀疑。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (3, '2025-01-16 20:45:58.680', '2025-01-16 20:45:58.680', '2025-01-16 20:47:24.718', 1, '', 0, '张伟作为AI技术的拥护者，帮助李明深入理解AI的本质，并提出结合AI与传统创作方式的建议，试图找到平衡点。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (4, '2025-01-16 20:45:58.680', '2025-01-16 20:45:58.680', '2025-01-16 20:47:24.718', 1, '', 0, '王丽作为戏剧社社长，最初对AI生成剧本持怀疑态度，但在看到李明的成功后，开始接受AI工具，并与李明共同探讨AI对文化创作的影响。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (5, '2025-01-16 20:45:58.680', '2025-01-16 20:45:58.680', '2025-01-16 20:47:24.718', 1, '', 0, '陈老师作为李明的文学教授，对AI技术持保守态度，认为真正的创造力来自于人类自身，多次与李明讨论AI对文化传承的影响。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (6, '2025-01-16 20:45:58.680', '2025-01-16 20:45:58.680', '2025-01-16 20:47:24.718', 1, '', 0, '刘洋作为\'创想无限\'的重度用户，陷入创作瓶颈。在李明的启发下，他开始尝试将AI与个人创意结合，最终创作出获奖短片。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (7, '2025-01-16 20:45:58.680', '2025-01-16 20:45:58.680', '2025-01-16 20:47:24.718', 1, '', 0, '赵婷对AI技术持批判态度，认为过度依赖AI会影响人类的心理健康。她的观点帮助李明更全面地理解AI的双刃剑特性。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (8, '2025-01-16 20:45:58.680', '2025-01-16 20:45:58.680', '2025-01-16 20:47:24.718', 1, '', 0, '孙浩作为校园黑客社的社长，开发小程序优化\'创想无限\'的使用体验，为李明和他的朋友们提供了重要的技术支持。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (9, '2025-01-16 20:45:58.680', '2025-01-16 20:45:58.680', '2025-01-16 20:47:24.718', 1, '', 0, '周梅作为图书馆管理员，组织读书会和文学沙龙，鼓励学生们回归传统阅读方式，对李明决定回归传统创作方式产生了重要影响。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (10, '2025-01-16 20:45:58.680', '2025-01-16 20:45:58.680', '2025-01-16 20:47:24.718', 1, '', 0, '李明和他的朋友们决定结合AI与传统创作方式，在校园戏剧社的演出中展示了一部由人类与AI共同创作的剧本，赢得了观众的热烈掌声。');
