INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (32, '2025-01-16 20:49:13.085', '2025-01-19 19:45:29.796', NULL, 1, 'AI浪潮的兴起', 2, '2025年，全球范围内人工智能和大语言模型的热潮席卷而来，大学生李明偶然发现并开始使用一款名为\'创想无限\'的AI生成内容应用，迅速在校园戏剧社中取得成功。');
