INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (34, '2025-01-16 20:49:13.085', '2025-01-22 20:18:34.945', NULL, 1, '深入探讨', 7, '李明与好友张伟、戏剧社社长王丽以及文学教授陈老师深入讨论AI对文化创作的影响，开始反思AI生成内容的局限性。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (35, '2025-01-16 20:49:13.085', '2025-01-16 20:49:13.085', NULL, 1, '创作的瓶颈', 0, '刘洋，一个重度依赖AI生成脚本的电影制作学生，遇到了创作瓶颈，这进一步加深了李明对AI依赖的担忧。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (36, '2025-01-16 20:49:13.085', '2025-01-16 20:49:13.085', NULL, 1, '心理学的视角', 0, '心理学学生赵婷从心理健康的角度出发，批判过度依赖AI可能带来的负面影响，为李明提供了新的思考角度。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (37, '2025-01-16 20:49:13.085', '2025-01-16 20:49:13.085', NULL, 1, '技术的支持', 0, '孙浩，校园黑客社的社长，开发了优化\'创想无限\'使用体验的小程序，帮助李明和他的朋友们更好地理解和使用AI工具。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (38, '2025-01-16 20:49:13.085', '2025-01-16 20:49:13.085', NULL, 1, '回归传统', 0, '在周梅，校园图书馆管理员的鼓励下，李明和他的朋友们开始尝试回归传统创作方式，同时结合AI的优势，创造出更具深度和个性的作品。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (39, '2025-01-16 20:49:13.085', '2025-01-16 20:49:13.085', NULL, 1, '人机共创', 0, '李明和他的朋友们在校园戏剧社的演出中展示了一部由人类与AI共同创作的剧本，赢得了观众的热烈掌声，展示了AI与人类创造力结合的可能性。');
INSERT INTO `chapters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `tittle`, `version_id`, `description`) VALUES (40, '2025-01-16 20:49:13.085', '2025-01-16 20:49:13.085', NULL, 1, '新的开始', 0, '通过一系列的讨论和反思，李明和他的朋友们逐渐看透了AI的本质，决定不再依赖AI生成内容，而是结合AI的优势，推动文化创作向前发展。');
COMMIT;

-- ----------------------------
-- Table structure for character_relation_ships
-- ----------------------------
DROP TABLE IF EXISTS `character_relation_ships`;
CREATE TABLE `character_relation_ships` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `created_at` datetime(3) DEFAULT NULL,
  `updated_at` datetime(3) DEFAULT NULL,
  `deleted_at` datetime(3) DEFAULT NULL,
  `first_character_id` bigint unsigned DEFAULT NULL,
  `second_character_id` bigint unsigned DEFAULT NULL,
  `content` varchar(1000) DEFAULT NULL,
  `name` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_character_relation_ships_deleted_at` (`deleted_at`),
  KEY `fk_character_relation_ships_second_character` (`second_character_id`),
  KEY `fk_character_relation_ships_first_character` (`first_character_id`),
  CONSTRAINT `fk_character_relation_ships_first_character` FOREIGN KEY (`first_character_id`) REFERENCES `characters` (`id`),
  CONSTRAINT `fk_character_relation_ships_second_character` FOREIGN KEY (`second_character_id`) REFERENCES `characters` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- ----------------------------
-- Records of character_relation_ships
-- ----------------------------
BEGIN;
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (1, '2025-01-14 18:55:29.804', '2025-01-14 18:55:29.804', NULL, 31, 32, '李明和张伟是大学室友，也是好朋友。李明主修文学，对戏剧充满热情，而张伟则是计算机科学专业的学生，对AI技术充满热情。两人性格互补，李明开朗善于交际，张伟冷静善于分析。在AI生成内容盛行的背景下，李明开始使用\'创想无限\'应用生成剧本，并在校园戏剧社的演出中获得成功。然而，随着对AI依赖的加深，李明开始质疑这种便利背后的代价。张伟作为AI技术的拥护者和理解者，帮助李明深入理解AI的本质，并在关键时刻提出结合AI与传统创作方式的建议，帮助李明找到平衡点。最终，两人在校园戏剧社的演出中共同展示了一部由人类与AI共同创作的剧本，赢得了观众的认可，也巩固了他们之间的友谊。', '室友兼好友');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (2, '2025-01-14 19:20:45.530', '2025-01-14 19:20:45.530', NULL, 31, 34, '李明和陈老师之间的关系是典型的师生关系，但在这个故事中，他们的关系还包含了导师与探索者、保守与革新之间的对话。陈老师作为李明的文学课教授，不仅传授文学知识，还在李明探索AI创作初期时，提供了关于文化传承和人类创造力的深刻见解。随着剧情的发展，陈老师成为李明反思AI创作便利性背后问题的重要引导者。在故事的高潮部分，陈老师与李明多次讨论AI对文化的影响，鼓励他回归传统创作方式。最终，李明在陈老师的启发下，与朋友们一起将AI作为一种工具，而非替代品，创作出融合传统与现代的剧本，展现了师生之间在思想上的碰撞与融合，以及共同成长的过程。', '师生');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (3, '2025-01-14 19:34:57.691', '2025-01-14 19:34:57.691', NULL, 33, 31, '王丽和李明是校园戏剧社的合作伙伴。王丽作为社长，对戏剧有着严格的艺术标准，而李明则是一名文学专业的学生，对戏剧充满热情。李明最初被\'创想无限\'应用吸引，使用AI生成的剧本在戏剧社的演出中取得了成功，这使得王丽对AI工具的态度从怀疑转为接受。然而，随着AI生成内容的普及，两人共同探讨了AI对文化创作的影响，并在最终决定回归传统创作方式的过程中，王丽给予了李明坚定的支持。他们的合作不仅限于戏剧创作，更是在探索艺术与技术融合的道路上共同成长，最终在校园戏剧社的演出中展示了人机共同创作的成果，赢得了广泛赞誉。', '合作伙伴');
INSERT INTO `character_relation_ships` (`id`, `created_at`, `updated_at`, `deleted_at`, `first_character_id`, `second_character_id`, `content`, `name`) VALUES (4, '2025-01-14 19:53:57.470', '2025-01-14 19:53:57.470', NULL, 32, 38, '张伟和孙浩之间的关系是基于他们对AI技术的共同热情和专业知识。张伟作为计算机科学专业的学生，对AI技术有着深入的理解和分析能力，而孙浩作为校园黑客社的社长，他的技术能力和创新思维为团队提供了强大的技术支持。在剧情中，张伟帮助李明深入理解AI的本质，而孙浩则通过开发小程序优化\'创想无限\'的使用体验，共同支持李明和他的朋友们在创作上的探索。他们的合作不仅限于技术层面，还包括对AI与人类创造力结合的深入讨论和建议，最终帮助团队找到了一种结合AI与传统创作方式的平衡点，推动了故事的高潮和解决。', '技术伙伴与创意顾问');
COMMIT;

-- ----------------------------
-- Table structure for characters
-- ----------------------------
DROP TABLE IF EXISTS `characters`;
CREATE TABLE `characters` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `created_at` datetime(3) DEFAULT NULL,
  `updated_at` datetime(3) DEFAULT NULL,
  `deleted_at` datetime(3) DEFAULT NULL,
  `project_id` bigint unsigned DEFAULT NULL,
  `name` varchar(50) DEFAULT NULL,
  `description` varchar(200) DEFAULT NULL,
  `avatar` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_characters_deleted_at` (`deleted_at`),
  KEY `fk_characters_project` (`project_id`),
  CONSTRAINT `fk_characters_project` FOREIGN KEY (`project_id`) REFERENCES `projects` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=40 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- ----------------------------
-- Records of characters
-- ----------------------------
BEGIN;
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (1, NULL, NULL, NULL, 1, 'ceshi', 'ceshi', NULL);
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (31, '2025-01-09 14:36:07.538', '2025-01-15 14:41:16.934', NULL, 1, '李明', '李明是一名大学生，主修文学，对戏剧有着浓厚的兴趣。他性格开朗，善于交际，但内心深处对未来的不确定性感到焦虑。偶然发现\'创想无限\'应用后，他被其强大的功能所吸引，开始使用AI生成剧本，并在校园戏剧社的演出中获得巨大成功。然而，随着AI生成内容的普及，李明逐渐意识到这种便利背后隐藏的问题，最终决定回归传统创作方式。', '20250115_9f9952.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (32, '2025-01-09 14:36:07.538', '2025-01-15 14:33:53.045', NULL, 1, '张伟', '张伟是李明的室友兼好友，计算机科学专业的学生。他对AI技术充满热情，是\'创想无限\'应用的早期用户之一。张伟性格冷静，善于分析问题，经常与李明讨论AI的优缺点。在高潮部分，张伟帮助李明深入理解AI的本质，并在解决部分提出结合AI与传统创作方式的建议。', '20250115_facb48.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (33, '2025-01-09 14:36:07.538', '2025-01-15 14:32:48.746', NULL, 1, '王丽', '王丽是校园戏剧社的社长，戏剧专业的学生。她性格坚定，对艺术创作有着极高的要求。王丽最初对\'创想无限\'应用持怀疑态度，但在看到李明使用AI生成的剧本后，逐渐接受了这一新工具。在高潮部分，王丽与李明一起探讨AI对文化创作的影响，并在解决部分支持李明回归传统创作方式的决定。', '20250115_145ccb.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (34, '2025-01-09 14:36:07.538', '2025-01-15 14:28:50.300', NULL, 1, '陈老师', '陈老师是李明的文学课教授，年近五十，经验丰富。他对AI技术持保守态度，认为真正的创造力来自于人类自身。陈老师在剧情中多次与李明讨论AI对文化传承的影响，并在解决部分鼓励李明和他的朋友们回归传统创作方式。', '20250115_83ae7b.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (35, '2025-01-09 14:36:07.538', '2025-01-09 14:36:07.538', NULL, 1, 'AI助手', 'AI助手是\'创想无限\'应用的核心功能，能够快速生成剧本、小说等内容作品。它没有实体形象，但在剧情中扮演了重要角色。AI助手的强大功能吸引了大量用户，但也引发了关于人类创造力和文化传承的担忧。在解决部分，AI助手成为李明和他的朋友们结合AI与传统创作方式的工具。', '');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (36, '2025-01-12 19:53:33.720', '2025-01-15 14:43:11.133', NULL, 1, '刘洋', '刘洋是一名大三学生，主修电影制作。他性格内向，但对于电影艺术有着极高的热情和独到的见解。刘洋是\'创想无限\'应用的重度用户，经常利用AI生成的脚本拍摄短片。他对AI技术的依赖让他陷入创作瓶颈，但在李明的启发下，他开始尝试将AI与个人创意结合，最终创作出获奖短片。', '20250115_ef0444.webp');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (37, '2025-01-12 19:53:33.720', '2025-01-12 19:53:33.720', NULL, 1, '赵婷', '赵婷是李明的同班同学，主修心理学。她对AI技术持批判态度，认为过度依赖AI会影响人类的心理健康。赵婷经常参与校园辩论，讨论AI对社会的潜在影响。她的观点帮助李明和他的朋友们更全面地理解了AI的双刃剑特性。', '');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (38, '2025-01-12 19:53:33.720', '2025-01-12 19:53:33.720', NULL, 1, '孙浩', '孙浩是校园黑客社的社长，计算机工程专业的研究生。他对AI技术有着深入的研究，经常开发一些小程序来优化\'创想无限\'的使用体验。孙浩性格幽默，喜欢挑战技术极限，他的技术支持和创新思维为李明和他的朋友们提供了重要的帮助。', '');
INSERT INTO `characters` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_id`, `name`, `description`, `avatar`) VALUES (39, '2025-01-12 19:53:33.720', '2025-01-12 19:53:33.720', NULL, 1, '周梅', '周梅是校园图书馆的管理员，对传统文学有着深厚的热爱。她经常组织读书会和文学沙龙，鼓励学生们回归纸质书籍和传统阅读。周梅的观点和行为对李明决定回归传统创作方式产生了重要影响。', '');
COMMIT;

