-- ----------------------------
-- Table structure for projects
-- ----------------------------
DROP TABLE IF EXISTS `projects`;
CREATE TABLE `projects` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `created_at` datetime(3) DEFAULT NULL,
  `updated_at` datetime(3) DEFAULT NULL,
  `deleted_at` datetime(3) DEFAULT NULL,
  `project_name` varchar(50) DEFAULT NULL,
  `social_story` varchar(500) DEFAULT NULL,
  `start` varchar(400) DEFAULT NULL,
  `high_point` varchar(400) DEFAULT NULL,
  `resolved` varchar(400) DEFAULT NULL,
  `style` json DEFAULT NULL,
  `types` varchar(40) DEFAULT NULL,
  `cover_image` varchar(200) DEFAULT NULL,
  `market_people` json DEFAULT NULL,
  `custom_prompt` varchar(200) DEFAULT NULL,
  `team_id` bigint unsigned DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_projects_deleted_at` (`deleted_at`),
  KEY `fk_projects_team` (`team_id`),
  CONSTRAINT `fk_projects_team` FOREIGN KEY (`team_id`) REFERENCES `teams` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

-- ----------------------------
-- Records of projects
-- ----------------------------
BEGIN;
INSERT INTO `projects` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_name`, `social_story`, `start`, `high_point`, `resolved`, `style`, `types`, `cover_image`, `market_people`, `custom_prompt`, `team_id`) VALUES (1, '2025-01-03 20:33:13.331', '2025-01-22 21:13:10.237', NULL, '创剧星球', '2025年，人工智能和大语言模型席卷全球，掀起了LLM热潮。社会各阶层对AI的依赖日益加深，从日常生活到工作学习，AI无处不在。大学生群体成为这一变革的先锋，他们既兴奋于技术的无限可能，又对未来的不确定性感到焦虑。在这个背景下，AI生成的内容开始在文化创作领域占据重要地位，但也引发了对人类创造力和文化传承的担忧。', '一家中国互联网初创企业推出了一款集成AI生成剧本、小说等内容作品的Web应用，名为\'创想无限\'。这款应用迅速在大学生群体中走红，成为他们创作和娱乐的新宠。主角李明是一名大学生，他偶然发现了这款应用，并被其强大的功能所吸引。他开始使用AI生成剧本，并在校园戏剧社的演出中获得巨大成功。然而，随着AI生成内容的普及，李明逐渐意识到这种便利背后隐藏的问题。', '一家中国互联网初创企业推出了一款集成AI生成剧本、小说等内容作品的Web应用，名为\'创想无限\'。这款应用迅速在大学生群体中走红，成为他们创作和娱乐的新宠。主角李明是一名大学生，他偶然发现了这款应用，并被其强大的功能所吸引。他开始使用AI生成剧本，并在校园戏剧社的演出中获得巨大成功。然而，随着AI生成内容的普及，李明逐渐意识到这种便利背后隐藏的问题。', '经过一系列的讨论和反思，李明和他的朋友们逐渐看透了AI的本质。他们意识到，AI只是一种工具，真正的创造力仍然来自于人类自身。他们决定不再依赖AI生成内容，而是重新回归到传统创作方式中，结合AI的优势，创造出更具深度和个性的作品。最终，李明和他的朋友们在校园戏剧社的演出中，展示了一部由人类与AI共同创作的剧本，赢得了观众的热烈掌声。人们逐渐明白，AI并不会取代人类文明，而是成为人类文明的一部分，推动其向前发展。', '[\"动作\", \"荒诞\", \"励志\"]', '剧情短片脚本', '20250122_c07297.webp', '[\"大学生\"]', '', 1);
INSERT INTO `projects` (`id`, `created_at`, `updated_at`, `deleted_at`, `project_name`, `social_story`, `start`, `high_point`, `resolved`, `style`, `types`, `cover_image`, `market_people`, `custom_prompt`, `team_id`) VALUES (2, '2025-01-06 19:51:26.459', '2025-01-06 19:51:26.459', NULL, '', '', '', '', '', '[\"武侠\", \"励志\", \"神秘\"]', '喜剧小品剧本', '', '[\"中老年人\", \"家庭观众\", \"男性\"]', '', 1);
COMMIT;

