const quickStartClickHandler = () => {
  const userStore = useUserStore()
  if (userStore.isLogin) {
    router.push('/platform')
  }
  get('/api/user/me', {},
      (message, data) => {
        userStore.login(data)
      },
      (message, data) => {

      },
      (message, data) => {

      }
  )
  if (!userStore.isLogin) {
    ElMessage.info('您还尚未登录，请先登录！');
    setTimeout(() => {
      router.push('/auth/login')
    }, 1000)
  } else {
    router.push('/platform')
  }
}
